import React from "react";
import { Button } from "..//ButtonElement";
import {
  InfoContainer,
  InfoRow,
  Column1,
 
  InfoWrapper,
  TextWrapper,
  TopLine,
  Heading,
  Subtitle,
  BtnWrap,
  Column2,
  ImgWrap,
  Img,
} from "./InfoElement";

const InfoSection = ({
  lightBg,
  id,
  alt,
  img,
  primary,
  dark,
  dark2,
  imgStart,
  topLine,
  lightText,
  headline,
  darkText,
  description,
  buttonLable,
}) => {
  return (
    <>
      <InfoContainer lightBg={lightBg} id={id}>
        <InfoWrapper>
          <InfoRow imgStart={imgStart}>
            <Column1>
              <TextWrapper>
                <TopLine>{topLine}</TopLine>
                <Heading lightText={lightText}>{headline}</Heading>
                <Subtitle darkText={darkText}>{description}</Subtitle>
                <BtnWrap>
                  <Button font-weight='600'
                    to="home"
                    dmooth={true}
                    duration={500}
                    spy={true}
                    exact={true}
                    offset={-80}
                    primary={primary ? 1 : 0}
                    dark = {dark ? 1 : 0}
                    dark2 = {dark2 ? 1 : 0}
                  >
                    {buttonLable}
                  </Button>
                </BtnWrap>
              </TextWrapper>
            </Column1>
            <Column2>
              <ImgWrap>
                <Img src={img} alt={alt} />
              </ImgWrap>
            </Column2>
          </InfoRow>
        </InfoWrapper>
      </InfoContainer>
    </>
  );
};

export default InfoSection;
